      <span class="token function">area</span><span class="token punctuation">(</span>r1<span class="token punctuation">)</span><span class="token punctuation">,</span> <span class="token function">area</span><span class="token punctuation">(</span>r2<span class="token punctuation">)</span>
    <span class="token punctuation">)</span><span class="token punctuation">;</span>


