    <span class="token keyword">function</span> <span class="token function">area1</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
      <span class="token comment">// this: 运行时服务的对象, 所在的</span>
      <span class="token keyword">return</span> <span class="token keyword">this</span><span class="token punctuation">.</span>length <span class="token operator">*</span> <span class="token keyword">this</span><span class="token punctuation">.</span>width
    <span class="token punctuation">}</span>

    <span class="token comment">// 1. 把 area1 存储到对象中 :  上门服务</span>
