      <span class="token keyword">return</span> <span class="token keyword">this</span><span class="token punctuation">.</span>radius <span class="token operator">*</span> <span class="token number">3.14</span> <span class="token operator">*</span> <span class="token number">2</span>
    <span class="token punctuation">}</span>

