    <span class="token comment">// 块级作用域就是 函数作用域的 简化版本</span>

    <span class="token comment">// 以前: 用匿名函数自调用语法生成作用域</span>
    <span class="token comment">// (function () { })()</span>

    <span class="token comment">// 现在: 用 {} 配合 let/const 即可形成块级作用域</span>
    <span class="token punctuation">{</span>
      <span class="token keyword">let</span> a <span class="token operator">=</span> <span class="token number">10</span>
      <span class="token keyword">const</span> b <span class="token operator">=</span> <span class="token number">20</span>
      console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span><span class="token number">111</span><span class="token punctuation">)</span>
    <span class="token punctuation">}</span>

    <span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">let</span> i <span class="token operator">=</span> <span class="token number">0</span><span class="token punctuation">;</span> i <span class="token operator">&lt;</span> <span class="token number">10</span><span class="token punctuation">;</span> i<span class="token operator">++</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
      console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>i<span class="token punctuation">)</span>
    <span class="token punctuation">}</span>


    console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>window<span class="token punctuation">)</span>

    <span class="token comment">// 私有属性: 函数作用域 诞生私有的变量, 通过闭包机制存储在函数里</span>
    <span class="token keyword">var</span> show <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token keyword">function</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
      <span class="token keyword">var</span> _count <span class="token operator">=</span> <span class="token number">0</span>

      <span class="token keyword">return</span> <span class="token keyword">function</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
        _count<span class="token operator">++</span>
        console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span><span class="token string">'次数:'</span><span class="token punctuation">,</span> _count<span class="token punctuation">)</span>
      <span class="token punctuation">}</span>
    <span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">(</span><span class="token punctuation">)</span>

    console<span class="token punctuation">.</span><span class="token function">dir</span><span class="token punctuation">(</span>show<span class="token punctuation">)</span> <span class="token comment">//查看scopes</span>
    <span class="token function">show</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
    <span class="token function">show</span><span class="token punctuation">(</span><span class="token punctuation">)</span>

    <span class="token comment">///////////////////////////////////////</span>
    <span class="token comment">// 块级实现私有属性</span>
    <span class="token punctuation">{</span>
