      <span class="token keyword">function</span> <span class="token function">talk</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
        _count<span class="token operator">++</span>
        console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span><span class="token string">'次数:'</span><span class="token punctuation">,</span> _count<span class="token punctuation">)</span><span class="token punctuation">;</span>
      <span class="token punctuation">}</span>
    <span class="token punctuation">}</span>

    console<span class="token punctuation">.</span><span class="token function">dir</span><span class="token punctuation">(</span>talk<span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token function">talk</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
    <span class="token function">talk</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
    <span class="token function">talk</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
  </span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>script</span><span class="token punctuation">></span></span>
