<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>body</span><span class="token punctuation">></span></span>
  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>script</span><span class="token punctuation">></span></span><span class="token script"><span class="token language-javascript">
    <span class="token comment">// ES6 提供的 匿名函数语法</span>

    <span class="token comment">// 之前的匿名函数</span>
    <span class="token comment">// function() { }</span>
    <span class="token comment">// 箭头函数</span>
    <span class="token comment">// () => { }</span>

    <span class="token comment">// 官方提供语法糖:</span>
    <span class="token keyword">var</span> <span class="token function-variable function">show</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token parameter">x</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
      <span class="token keyword">return</span> <span class="token number">2</span> <span class="token operator">*</span> x
    <span class="token punctuation">}</span>
    <span class="token comment">// 糖1: 形参有且只有1个时, 可以省略()</span>
    <span class="token keyword">var</span> <span class="token function-variable function">show</span> <span class="token operator">=</span> <span class="token parameter">x</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
      <span class="token keyword">return</span> <span class="token number">2</span> <span class="token operator">*</span> x
    <span class="token punctuation">}</span>
    <span class="token comment">// 糖2: 函数体仅有一行时,可以省略 {return }</span>
    <span class="token keyword">var</span> <span class="token function-variable function">show</span> <span class="token operator">=</span> <span class="token parameter">x</span> <span class="token operator">=></span> <span class="token number">2</span> <span class="token operator">*</span> x

    console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span><span class="token function">show</span><span class="token punctuation">(</span><span class="token number">10</span><span class="token punctuation">)</span><span class="token punctuation">)</span>

    <span class="token comment">//////////////////////////</span>
    <span class="token comment">//练习: 尝试用语法糖 简化下方代码</span>
    <span class="token keyword">var</span> <span class="token function-variable function">a</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token parameter">y</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span> <span class="token keyword">return</span> y <span class="token operator">*</span> y <span class="token punctuation">}</span>
    <span class="token keyword">var</span> <span class="token function-variable function">a</span> <span class="token operator">=</span> <span class="token parameter">y</span> <span class="token operator">=></span> y <span class="token operator">*</span> y

    <span class="token keyword">var</span> <span class="token function-variable function">b</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token parameter">x<span class="token punctuation">,</span> y</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span> <span class="token keyword">return</span> x <span class="token operator">+</span> y <span class="token punctuation">}</span>
    <span class="token keyword">var</span> <span class="token function-variable function">b</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token parameter">x<span class="token punctuation">,</span> y</span><span class="token punctuation">)</span> <span class="token operator">=></span> x <span class="token operator">+</span> y

    <span class="token keyword">var</span> <span class="token function-variable function">c</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token parameter">m<span class="token punctuation">,</span> n</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
      <span class="token keyword">return</span> <span class="token punctuation">{</span> <span class="token literal-property property">m</span><span class="token operator">:</span> m<span class="token punctuation">,</span> <span class="token literal-property property">n</span><span class="token operator">:</span> n <span class="token punctuation">}</span>
    <span class="token punctuation">}</span>
    <span class="token comment">// 问题: 对象的{} 被误认为是 函数的{}, 产生歧义</span>
    <span class="token comment">// 解决: 用()括起来, 从格式上避免歧义</span>

    <span class="token comment">// { 属性名: 值 }.  巧合: 值是变量, 变量名和属性名相同</span>
    <span class="token keyword">var</span> <span class="token function-variable function">c</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token parameter">m<span class="token punctuation">,</span> n</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">(</span><span class="token punctuation">{</span> <span class="token literal-property property">m</span><span class="token operator">:</span> m<span class="token punctuation">,</span> <span class="token literal-property property">n</span><span class="token operator">:</span> n <span class="token punctuation">}</span><span class="token punctuation">)</span>
    <span class="token comment">// 语法糖: 属性名和变量名一样, 则可以合写</span>
    <span class="token keyword">var</span> <span class="token function-variable function">c</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token parameter">m<span class="token punctuation">,</span> n</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">(</span><span class="token punctuation">{</span> m<span class="token punctuation">,</span> n <span class="token punctuation">}</span><span class="token punctuation">)</span>

    console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>
      <span class="token function">c</span><span class="token punctuation">(</span><span class="token number">10</span><span class="token punctuation">,</span> <span class="token number">20</span><span class="token punctuation">)</span>
    <span class="token punctuation">)</span>

    <span class="token comment">// 糖:</span>
    <span class="token keyword">var</span> emp <span class="token operator">=</span> <span class="token punctuation">{</span>
      <span class="token comment">// 可以省略 : function</span>
      <span class="token comment">// show: function () { alert("Hello!") }</span>
      <span class="token function">show</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span> <span class="token function">alert</span><span class="token punctuation">(</span><span class="token string">"Hello!"</span><span class="token punctuation">)</span> <span class="token punctuation">}</span>
    <span class="token punctuation">}</span>

    emp<span class="token punctuation">.</span><span class="token function">show</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
