  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>script</span><span class="token punctuation">></span></span><span class="token script"><span class="token language-javascript">
    <span class="token comment">// 关于function的this</span>
    <span class="token comment">// 对象.方法名(): this是前方的对象</span>
    <span class="token comment">// 方法名(): this是window</span>
    <span class="token comment">// new 方法名(): this是构造出的 实例对象</span>

    <span class="token comment">// 关于箭头函数的this</span>
    <span class="token comment">// 箭头函数没有this, 按照作用域链的就近原则到上级作用域查找</span>
    <span class="token keyword">var</span> emp <span class="token operator">=</span> <span class="token punctuation">{</span>
      <span class="token literal-property property">ename</span><span class="token operator">:</span> <span class="token string">"小明"</span><span class="token punctuation">,</span>
      <span class="token function">show</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
        <span class="token comment">// 箭头函数没有this, 所以使用上级 show 函数中的this</span>
        <span class="token comment">// emp.show() :  所以 show的this 就是emp</span>
        <span class="token keyword">var</span> <span class="token function-variable function">a</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
          console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span><span class="token string">'this:'</span><span class="token punctuation">,</span> <span class="token keyword">this</span><span class="token punctuation">)</span>
          console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span><span class="token keyword">this</span> <span class="token operator">==</span> emp<span class="token punctuation">)</span>
        <span class="token punctuation">}</span>
        <span class="token function">a</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
      <span class="token punctuation">}</span>
    <span class="token punctuation">}</span>

    emp<span class="token punctuation">.</span><span class="token function">show</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
  </span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>script</span><span class="token punctuation">></span></span>
