    <span class="token comment">// 圆角: border-radius 修改为 10px</span>
    <span class="token function">$</span><span class="token punctuation">(</span><span class="token string">'li'</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">css</span><span class="token punctuation">(</span><span class="token string">'border-radius'</span><span class="token punctuation">,</span> <span class="token number">10</span><span class="token punctuation">)</span>
